const login = async () => {
  const username = "user";
  const password = "user123";

  // Codifica o nome de usuário e senha em base64
  const token = btoa(`${username}:${password}`);

  const response = await fetch('http://localhost:8080/api/protected-resource', {
    method: 'GET',
    headers: {
      'Authorization': `Basic ${token}`,  // Adiciona o cabeçalho de autenticação
      'Content-Type': 'application/json'
    }
  });

  if (response.ok) {
    const data = await response.json();
    console.log("Requisição bem-sucedida:", data);
  } else {
    console.error("Falha na autenticação:", response.status);
  }
};
